package com.example.rabbitmqhello;import org.springframework.amqp.core.Queue;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * RabbitConfig * * @author eliefly * @date 2018-03-29 */@Configurationpublic class RabbitConfig {    @Bean    public Queue helloQueue() {        return new Queue("hello");    }}